download() {
  local dir="$1"
  local url="$2"
  local filename="$3"
  mkdir -p "$dir"
  if [ ! -f "$dir/$filename" ]; then
    echo "Downloading: $filename -> $dir/"
    curl -sL "$url" -o "$dir/$filename"
  else
    echo "Skip (exists): $dir/$filename"
  fi
}

